choose.when:
where size like #{size}
	and name like concat('%',#{keyword},'%')
	and price between #{minprice} and #{maxprice}

mapper.update:
update product
	set ea = ea - #{count}
	where productID = #{productID}

update product
	set evalSum = evalSum + #{evalRate}
	where productID = #{productID}

mapper.select:
select evalSum
	from product
	where productID = #{id}
